import React,{useState,useContext} from 'react'
import Nav from './Nav'
import {Taka,Stars} from "./Components"
import axios from 'axios'
import Navigation from './Navigation-ash'
import {useLocation} from 'react-router-dom'
import Context from './Context'

const Reviewer = () => {
	let location = useLocation()
	let data = location.state
	let context = useContext(Context)
    let id = context.user? context.user : null
    let userid = id.user_id
    console.log(id)
	let url2 = 'http://127.0.0.1:8000'
	let url = 'http://127.0.0.1:8000/addrev/'
	let [star,setStar] = useState(0)
	let [writing,setWriting]=useState('')
	let [img,setImg]=useState()
	let pk = data.id
	let sender = {userid,pk,writing,img,star} 
	let HandlePost =()=>{
            //axios function
		    //productID,username,star,writing,images

		console.log(sender.img,'image')
		axios.post(url,{
             userid:userid,
             pk:pk,
             writing:writing,
             img:img,
             star:star
		},
		{
           headers: {
               'content-type': 'multipart/form-data'
           }
        }
        )
		 .then((response)=>{

            console.log(response.data)
		 })
		 .catch((err)=>{
		 	console.log(err)

		 })
	}
	console.log(data)
	console.log(star)
	return (
		<div className="reviewer-page">
            	<Nav colour={"ash"} visible={true} stick={false} ></Nav>
			<div className="product-details">
				<img className="review-image" src={`${url2}${data.image}`} alt=""/>
				<div className="details">
				    <h1 className="title">{data.name}</h1>
					<h1 className="story"> {data.story}</h1>
					<Taka num={"one"} taka={data.price}></Taka>
					<Stars setStar={setStar} >

					</Stars>
				</div>
			</div>
			<div className="input">
				<input onChange={(e)=>setWriting(e.target.value)} value={writing}  placeholder="Write your own review"  type="text"  className="rev">

				</input>
				 <img src="images/edit.png" alt="" className="write"/>
			</div>
			<div className="buttons">

			   <div className="btn-2">
			        <input onChange={(e)=>{setImg(e.target.files)}}  type="file"/>
					<img src="images/photo-camera.png" alt=""/>

				</div>
				<div className="btn-1">
					<button onClick={HandlePost} >
						Post review
					</button>
				</div>

			</div>
		</div>
	)
}

export default Reviewer
