import React,{createContext,useState,useEffect} from 'react'
import {useNavigate} from 'react-router-dom'
import jwt_decode from 'jwt-decode'
import axios from 'axios'
let Context = createContext()

export let AuthProvider = ({children}) =>{

    let navigate = useNavigate()
    let localStorageRefresh = localStorage.getItem('refreshtoken')
    let localStorageAccess = localStorage.getItem('usertoken')
    let [refresh,setRefresh] = useState(()=>{
    	if(localStorageRefresh){
    		return JSON.parse(localStorageRefresh)
    	}
    	else{
    		return null
    	}
    })
	const [token, setToken] = useState(()=>{
    	if(localStorageAccess){
    		return JSON.parse(localStorageAccess)
    	}
    	else{
    		return null
    	}
    })
	//contains user id
	let [data,setData]= useState()
	// console.log(data.user_id)
	//

	let lifespan = 1000 * 60 * 4
	let url = 'http://127.0.0.1:8000/api/token/refresh/'

	useEffect(() => {
		let Now = setInterval(()=>{
          if(token!=null){
          axios.post(url,{'refresh':refresh})
		    .then((response)=>{
			 setToken(response.data.access)
			 localStorage.setItem('usertoken',JSON.stringify(token))
			 console.log('hello'+token)

		   })
		   .catch((err)=>{
			 console.log(err)
		   })
		  }
		  else{
		  	console.log('refresh failed')
		  }
		},lifespan)
		return () => {
			clearInterval(Now)
			console.log('cleared')
		};
	}, [token,refresh])


	let usersetter=(token)=>{
		let userdata = jwt_decode(token.access)
		console.log(userdata)
		if(userdata){
		setData(userdata)
		   if(data){
		     console.log(data.user_id)
		   }
	    }

	}


	let tokensetter=(sentToken)=>{

		if(sentToken){
		setToken(sentToken.access)
		setRefresh(sentToken.refresh)
		localStorage.setItem('usertoken',JSON.stringify(token))
		localStorage.setItem('refreshtoken',JSON.stringify(refresh))
        navigate('/')
       }
	}



	return(
        <Context.Provider value={{'function':tokensetter,'token':token,'user':data,'usersetter':usersetter}} >
        	{children}
        </Context.Provider>
	)
}

export default Context
