     console.log(num)

	let navoption = [1,2,3,4,5,6,7,8,9]
	return (
		<div className="bottom-nav-cont">
		 <img onClick={()=>setMargin(margin-100)} id="left" src="images/arrow-icon.png" alt="" className="arrow-icons"/>
		 <div className="cotainer">
		 	<div  className="num-nav">
		 	{navoption.map((navs,i)=>{
		 		return(
		 		<button style={{marginLeft:-margin}} className={num===i? 'btn-act' : 'btn-'} onClick={()=>{
		 			 setNum(i)

		 			 console.log(i)

                 }

		 	    }
		 	     key={i} >{navs}</button>
		 		)

		 	})}
		 </div>
		 </div>

		 <img onClick={()=>setMargin(margin+100)} src="images/arrow-icon.png" alt="" className="arrow-icons"/>

		</div>
	)
}

export default Bottom
