import { createStore } from "redux";
let initialState = {counter:0}
let reducer=(state=initialState,action)=>{
	switch(action.type){
	case "ADD": return {
		...state,
		counter:state.counter+1

	}
    default: return state
}
}
let store = createStore(reducer)

export default store
