import react ,{useState,useEffect,useRef}from 'react'
import Navigation from './Navigation'
import '../sass/main.scss'
import RunningOut from './RunningOut'
import MostPopular from "./MostPopular"
import Nav from './Nav'

let Landing = ()=>{
  let [isout,setIsout] = useState(false)
  // console.log(isout)

  return(
    <>
    <div  className="land">
      <Nav visible={isout} colour={'white'} ></Nav>
     <Home referer={setIsout}  ></Home>
     <RunningOut  ></RunningOut>
     <MostPopular></MostPopular>
    </div>

     </>
  )
}

let Home =(props)=>{
  let [buttonthere,setButtonthere] = useState(true)
  let myref = useRef();

   useEffect(() => {
        const handleScroll = event => {
          console.log(window.scrollY)
        // if(window.scrollY>0){
        //   setButtonthere(false)
        //   console.log('gese')

        // }

    };

    window.addEventListener('scroll', handleScroll);

    return () => {
       window.removeEventListener('scroll', handleScroll);
      let observer = new IntersectionObserver ((entries)=>{
        let entry = entries[0];
        props.referer(entry.isIntersecting)

      })
      observer.observe(myref.current)
    };
  }, [])
  return(
    <>
       <div className="page1">

           <div  ref={myref} className="hero-section">
           <div className="parts" id="part1">
             <Heading>

           </Heading>
           </div>
           <div className="parts" id="part2">
             <Popup >

           </Popup>
           </div>


           </div>

           <Scrolldown visibility={buttonthere} >

           </Scrolldown>

       </div>


    </>
  )
}
let Scrolldown = (props)=>{
  console.log(props.visibility)
  return(
    <>
      <button className={props.visibility?"arrow-down-button" : "arrow-down-button dissolves"}>
       <img src="images/arrow-icon.png" alt="" className="scrolldown"/>
      </button>
    </> 
  )
}

let Heading=()=>{

  return(
        <h1>
          largest 
          <span>manga</span> shop in bangladesh
        </h1>

  )
}
let Popup =()=>{

  return(

       <img src="images/mikasa.png" alt="" className="pop-img"/>

  )
}

export default Landing;
