import { createStore } from "redux";
let initialState = {data:[]}
let reducer=(state=initialState,action)=>{
	switch(action.type){
	case "ADD": return {
		...state,
		data:[...state.data , action.payload]

	}
    default: return state
}
}
let store = createStore(reducer)

export default store
